import destinationData from"../Data/destination.json";const destinationContent=destinationData.destination,renderCard=n=>{const t=document.querySelector(".container-card");t.innerHTML="",n.forEach((n=>{const{title:a,description:e,image:i,page:d}=n;t.innerHTML+=`\n    <div class="card-destination">\n      <img src="${i}" alt="${a}">\n\n      <div class="card__inner">\n        <h4 class="card-title">${a}</h4>\n        <p class="card__description">\n          ${e}\n          <div class="d-grid gap-2 d-md-flex justify-content-md-end">\n            <a href="${d}" class="btn btn-danger justify-content-end">Read More</a>\n          </div>\n        </p>\n      </div>\n    </div>\n    `}))};renderCard(destinationContent);